import { useEffect } from "react";
import { useSelector } from "react-redux";

import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";

function App() {
  const isOpenCart = useSelector((state) => state.cart.isOpenCart);
  const cart = useSelector((state) => state.cart);

  //every time app component render, the initial value will store the database, that would be problem because empty data will update all exsiting data every single time when app component render, so we need fix it
  useEffect(() => {
    const sendCartData = async () => {
      const response = await fetch(
        "https://react-http-3be24-default-rtdb.firebaseio.com/cart.json",
        {
          method: "PUT",
          body: JSON.stringify(cart),
        }
      );

      const responseData = await response.json();
    };
  }, [cart]);

  return (
    <Layout>
      {isOpenCart && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
