import { useSelector, useDispatch } from "react-redux";
import { cartAction } from "../../store/cart";

import classes from "./CartItem.module.css";

const CartItem = (props) => {
  const dispatch = useDispatch();
  const item = useSelector((state) => state.cart.item);
  const { title, price } = props.item;
  const total = item * price;

  const itemIncrementHandler = () => {
    dispatch(cartAction.itemIncrement());
  };

  const itemDecrementHandler = () => {
    if (item <= 0) {
      return;
    }
    dispatch(cartAction.itemDecrement());
  };

  return (
    <li className={classes.item}>
      <header>
        <h3>{title}</h3>
        <div className={classes.price}>
          ${total.toFixed(2)}{" "}
          <span className={classes.itemprice}>(${price.toFixed(2)}/item)</span>
        </div>
      </header>
      <div className={classes.details}>
        <div className={classes.quantity}>
          x <span>{item}</span>
        </div>
        <div className={classes.actions}>
          <button onClick={itemDecrementHandler}>-</button>
          <button onClick={itemIncrementHandler}>+</button>
        </div>
      </div>
    </li>
  );
};

export default CartItem;
