import { createSlice } from "@reduxjs/toolkit";

const uiSlice = createSlice({
  name: "ui",
  initialState: {
    isOpenCart: false,
    notification: null,
  },
  reducers: {
    openCart(state) {
      state.isOpenCart = !state.isOpenCart;
    },
    showNotificartion(state, action) {},
  },
});
export const uiActions = uiSlice.actions;

export default uiSlice.reducer;
