import { createSlice } from "@reduxjs/toolkit";

const initialCartState = {
  totalItems: 0,
  isOpenCart: false,
  cartItems: [
    {
      title: "",
      quantity: 0,
      price: 0,
      description: "",
    },
  ],
};

const CartSlice = createSlice({
  name: "cart",
  initialState: initialCartState,
  reducers: {
    itemIncrement(state) {
      state.item++;
    },
    itemDecrement(state) {
      state.item--;
    },
    addCart(state) {
      state.item++;
      state.totalItems += state.item;
    },
    totalItems(state) {
      state.totalItems = [];
    },
    openCart(state) {
      state.isOpenCart = !state.isOpenCart;
    },
  },
});

export const cartAction = CartSlice.actions;

export default CartSlice.reducer;
